package com.day1;

public class SumofelementsinArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int[] arr= {1,2,3,4,5,6};
         int sum=0;
         for(int n:arr) {
        	 sum+=n;
         }
       System.out.println("sum of elements of given array is "+sum);
	}

}
