package com.day2morning;

public class AssignmentOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=5;
		x+=5;
		System.out.println(x);
		x-=5;
		System.out.println(x);
		x*=5;
		System.out.println(x);
		x/=5;
		System.out.println(x);
		x%=5;
		System.out.println(x);
		

	}

}
